                Text("Pick a State:")
                    .font(.title2)
                Picker("", selection: $selectedState) {
                    ForEach(stateAbb, id: \.self) { usstate in
                        Text(usstate)
                            .font(.title2)
                    }
                }
                Button("Go!") {
                    roasterArray = stateDic[selectedState] ?? []
                }
                .buttonStyle(.borderedProminent)
            }
            
            
        }
        .onAppear {
            stateDic = ["AK":AK, "AL":AL, "AR":AR, "AZ":AZ, "CA":CA, "CO":CO, "CT":CT, "DC":DC, "DE":DE, "FL":FL, "GA":GA, "HI":HI, "IA":IA, "ID":ID, "IL":IL, "IN":IN, "KS":KS, "KY":KY, "LA":LA, "MA":MA, "MD":MD, "ME":ME, "MI":MI, "MN":MN, "MO":MO, "MS":MS, "MT":MT, "NC":NC, "ND":ND, "NE":NE, "NH":NH, "NJ":NJ, "NM":NM, "NV":NV, "NY":NY, "OH":OH, "OK":OK, "OR":OR, "PA":PA, "RI":RI, "SC":SC, "SD":SD, "TN":TN, "TX":TX, "UT":UT, "VA":VA, "VT":VT, "WA":WA, "WI":WI, "WV":WV, "WY":WY
            ]
            roasterArray = stateDic[selectedState] ?? []
        }
    }
    
    var searchResults: [String] {
        if searchText.isEmpty {
            return roasterArray
        } else {
            return roasterArray.filter {$0.contains(searchText)}
        }
    }
}

